import React, { Component } from 'react';
import axios from 'axios';

class UrlForm extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.inputRef = React.createRef();
  }

  handleClick = (e) => {
    e.preventDefault();

    const apiCall = async () => { 
      const response = await axios.get(this.state.videoId); 
    };

    apiCall();
  }
  render() {
    return (
      <div className="url-form">
        <input
          placeholder="write youtube url"
          ref={this.inputRef}
        />
        <button onClick={this.props.handleClick(this.inputRef.current)}>rearrange</button>
      </div>
    );
  }
}

export default UrlForm;
