import React, { Component } from 'react';
import axios from 'axios';

// ref : https://stackoverflow.com/questions/3452546/how-do-i-get-the-youtube-video-id-from-a-url
const getVideoId = videoUrl => {
  let regExp = /^.*(youtu\.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
  let match = videoUrl.match(regExp);
  if (match && match[2].length == 11) {
    return match[2];
  } else {
    return undefined;
  }
}
class UrlForm extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.inputRef = React.createRef();
  }

  shouldComponentUpdate(nextProps, nextState) {
    return false;
  }

  handleClick = (e) => {
    this.props.handleClick(getVideoId(e.target.value));
  }
  render() {
    return (
      <div className="url-form">
        <input
          placeholder="write youtube url"
          ref={this.inputRef}
        />
        <button onClick={this.props.handleClick(this.inputRef.current)}>rearrange</button>
      </div>
    );
  }
}

export default UrlForm;
