import './App.css';
import React from 'react';
import UrlForm from './components/UrlForm';
import VideoInfo from './components/VideoInfo';
import Comments from './components/Comments';

//youtube comment api
const apiKey = 'AIzaSyBhrKlcf07TRvzF5RPeKOxYSBC06TP-JUc';
const getCommentThreads = videoId => `https://www.googleapis.com/youtube/v3/commentThreads?part=snippet&key=${apiKey}\
                              &videoId=${videoId}&maxResults=100`
const getVideoInfo = videoId => `https://www.googleapis.com/youtube/v3/videos?part=snippet&key=${apiKey}\
                              &videoId=${videoId}`
const getChannelImgUrl = channelId => `https://www.googleapis.com/youtube/v3/channels?part=snippet&key=${apiKey}\
                              &id=${channelId}`

class App extends React.Component {
  state = {
    videoId: '',
    videoInfo: {
      videoTitle: '',
      videoThumbnail : '',
      description: '',
      channelImg : '',
    },
  }

  onChangeVideoId = (videoId) => {
    this.setState({videoId: videoId})
    const getVideoInfo = async() => {
      const videoInfo = await getVideoInfo(videoId);

      const channelId = videoInfo.items.snippet.channelId;
      const videoTitle = videoInfo.items.snippet.title;
      const description = videoInfo.items.snippet.description;
      const videoThumbnail = videoInfo.items.snippet.thumbnails.default.url;

      const channelInfo = await getChannelImgUrl(channelId);
      const channelImg = channelInfo.snippet.thumbnails.default.url;

      this.setState({ videoInfo : {videoTitle, videoThumbnail, description, channelImg}});
    }
  }

  render() {
    return (
      <div className="App">
          <UrlForm handleClick={this.onChangeVideoId}/>
      </div>
    );
  }
}

export default App;
