import React, { Component } from 'react';
import axios from 'axios';

//api key for youtube comment api
const apiKey = 'AIzaSyBhrKlcf07TRvzF5RPeKOxYSBC06TP-JUc';
const getUrl = videoId => `https://www.googleapis.com/youtube/v3/commentThreads?part=snippet&key=${apiKey}\
                              &videoId=6FNWI9z_5J4&maxResults=100`
const videoIdReg = /v=*?/

class UrlForm extends Component {
  state = {
    url: ''
  }
  handleChange = (e) => {
    this.setState({
      url: e.target.value
    });
  }
  onClick = (e) => {
    e.preventDefault();

    const apiCall = async () => { 
      let youtubeLink = this.state.url;
      let videoIdIndex = youtubeLink.search(videoIdReg) + 2;
      let videoId = youtubeLink.slice(videoIdIndex).split('&')[0];
      console.log(videoId)

      const response = await axios.get(getUrl()); 
      console.log(response); 
    };

    apiCall();

  }
  render() {
    return (
      <div className="url-form">
        <input
          placeholder="write youtube url"
          onChange={this.handleChange}
        />
        <button onClick={this.onClick}>rearrange</button>
      </div>
    );
  }
}

export default UrlForm;
