import React, { Component } from 'react';

const apiKey = 'AIzaSyBhrKlcf07TRvzF5RPeKOxYSBC06TP-JUc';
const getVideoPageInfo = (videoId) => `https://youtube.googleapis.com/youtube/v3/videos\
                                      ?part=id&id=${videoId}&key=${apiKey}`

// ref : https://stackoverflow.com/questions/3452546/how-do-i-get-the-youtube-video-id-from-a-url
const getVideoId = videoUrl => {
  let regExp = /^.*(youtu\.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
  let match = videoUrl.match(regExp);
  if (match && match[2].length === 11) {
    return match[2];
  } else {
    return undefined;
  }
}
class UrlForm extends Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
    // input 
    // undefined : no link
    // true : right link
    // false : wrong link
    this.state = {
      inputForm : undefined
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick = (e) => {
    console.log(this.inputRef.current.value);
    if (!this.inputRef.current) {
      return;
    } else if (this.inputRef.current.value.length === 0) {
      this.setState({
        inputForm: undefined
      })
    }
    const videoId = getVideoId(this.inputRef.current.value);
    if (!videoId || getVideoPageInfo(videoId).data.pageInfo.totalResults === 0) {
      this.setState({
        inputForm: false
      })
    } 
    this.props.handleClick(this.inputRef.current.value);
    this.setState({
      inputForm: true
    })
  }

  render() {
    return (
      <div className="url-form">
        <button onClick={this.handleClick}>rearrange</button>
        <input
          placeholder="write youtube url"
          ref={this.inputRef}
        />
        <div></div>
      </div>
    );
  }
}

export default UrlForm;
