import React, { Component } from 'react';
import axios from 'axios';

const apiKey = 'AIzaSyBhrKlcf07TRvzF5RPeKOxYSBC06TP-JUc';
const getVideoInfo = videoId => `https://www.googleapis.com/youtube/v3/videos?part=snippet&key=${apiKey}\
                              &videoId=${videoId}`
const getChannelImgUrl = channelId => `https://www.googleapis.com/youtube/v3/channels?part=snippet&key=${apiKey}\
                              &id=${channelId}`

class VideoInfo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            videoTitle : '',
            description: '',
            videoThumbnail: '',
            channelImg : ''
        }
    }

    shouldComponentUpdate(nextProps, nextState) {
        if (nextProps.videoId !== this.props.videoId) {
            this.updateVideoInfo(nextProps.videoId);
            return true;
        }
        return false;
    }

    updateVideoInfo = async(videoId) => {
        const videoInfo = await axios.get(getVideoInfo(videoId));

        const channelId = videoInfo.items.snippet.channelId;
        const videoTitle = videoInfo.items.snippet.title;
        const description = videoInfo.items.snippet.description;
        const videoThumbnail = videoInfo.items.snippet.thumbnails.default.url;

        const channelInfo = await axios.get(getChannelImgUrl(channelId));
        const channelImg = channelInfo.snippet.thumbnails.default.url;

        this.setState({ videoInfo : {videoTitle, videoThumbnail, description, channelImg}});
    }

    render() {
        return (
            this.props.videoId && (
                <div className="video-info">

                </div>)
        );
    }
}

export default VideoInfo;
