import React, { Component } from 'react';
import classNames from 'classnames';

const apiKey = 'AIzaSyBhrKlcf07TRvzF5RPeKOxYSBC06TP-JUc';
const getCommentThreads = videoId => `https://www.googleapis.com/youtube/v3/commentThreads?part=snippet&key=${apiKey}\
                                        &videoId=${videoId}&maxResults=100`
class CommentThread extends Component { 
    render() {
        // expand (true, false, undefined)
        // true: expand
        // false : show top comment
        // undefined : nothing to show
        const {expand, comments} = this.props;
        return (
            expand !== undefined && (
                <div  className={classNames('comment-thread', {expand : expand})}>
                    {!expand? comments[0]: comments}
                </div>
            )
        );
    }
}

export default CommentThread;
